mapper.select:
SELECT * FROM (
  select * from TBL_HOTEL NATURAL JOIN TBL_HOTEL_ATTACH where starrate is not null ORDER BY STARRATE DESC
  ) WHERE ROWNUM <= 10

mapper.update:
UPDATE TBL_REPLY SET 
	REGDATE = SYSDATE,
	STARRATE = #{starRate},
	CONTENT = #{content}
WHERE RNO = #{rno}

mapper.select:
SELECT *
	FROM (SELECT A.*, ROWNUM AS RNUM FROM (SELECT * FROM TBL_REPLY full join tbl_reply_attach Using(rno) WHERE STOREID= #{storeid} ORDER BY REGDATE DESC) A WHERE ROWNUM  <=  #{lastReplyCount})
WHERE RNUM  > #{firstReplyCount}
